mapper.select:
select id, parent_id, `name`, url, `type`, order_num, has_delete, create_time
from premission
where id = #{id,jdbcType=BIGINT}

select p.*,rp.id rpId from permission p left join (select * from role_permission where role_id = #{rid} ) rp on p.id = rp.pid
p.parent_id =#{parentId}
p.id in (${ids})
